package com.sunyu.redission.util;import com.power.common.model.CommonResult;import com.sunyu.redission.enums.ErrorCodeEnum;/** * * * @author yu on 2018/11/05. */public class ResultUtil {    /**     * return success     *     * @param data     * @return     */    public static <T> CommonResult<T> success(T data) {        CommonResult<T> result = new CommonResult();        result.setCode(ErrorCodeEnum.SUCCESS.getCode());        result.setSuccess(true);        result.setData(data);        result.setMessage(ErrorCodeEnum.SUCCESS.getDesc());        return result;    }    /**     * return error     *     * @param code error code     * @param msg  error message     * @return     */    public static CommonResult error(String code, String msg) {        CommonResult result = new CommonResult();        result.setCode(code);        result.setData("");        result.setMessage(msg);        return result;    }    /**     * use enum     *     * @param status     * @return     */    public static CommonResult error(ErrorCodeEnum status) {        return error(status.getCode(), status.getDesc());    }}